import React from "react";
import styled from "styled-components";

const MainFooterHolder = styled.div`
  width: 100%;
  margin-top: 35px;
`;

const FooterContent = styled.div`
  width: 100%;
  padding: 15px 0;
  max-width: 1250px;
  margin: 0 auto;
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  border-top: 1px solid #cacaca;
  @media (max-width: 750px) {
    flex-wrap: wrap;
    justify-content: center;
  }

`;

const LogoColumn = styled.div`
  width: 35%;
  padding: 15px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  img {
    width: 180px;
    margin-bottom: 15px;
  }
  p,
  h4 {
    margin-bottom: 0;
  }
  p {
    font-size: 12px;
  }
  @media (max-width: 950px) {
    width: 30%;
  }
  @media (max-width: 750px) {
    width: 100%;
  }
`;

const ContactColumn = styled.div`
  width: 25%;
  padding: 15px;
  display: flex;
  align-items: flex-start;
  justify-content: center;
  flex-direction: column;
  h3 {
    align-self: center;
    text-align: center;
  }
  img {
    width: 20px;
    margin-right: 10px;
  }
  p,
  h5 {
    margin-bottom: 0;
  }
  p {
    font-size: 12px;
  }
  @media (max-width: 950px) {
    width: 30%;
  }
  @media (max-width: 750px) {
    width: 45%;
  }
  @media (max-width: 500px) {
    width: 100%;
  }
`;

const ContactItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 25px;
  @media (max-width: 500px) {
    align-self: center;
  }
`;

const NewsletterColumn = styled.div`
  width: 30%;
  padding: 15px;
  display: flex;
  align-items: flex-start;
  justify-content: center;
  flex-direction: column;
  h3 {
    align-self: center;
    text-align: center;
  }
  p,
  h5 {
    margin-bottom: 0;
  }
  p {
    font-size: 12px;
    text-align: center;
  }
  @media (max-width: 950px) {
    width: 40%;
  }
  @media (max-width: 750px) {
    width: 45%;
  }
  @media (max-width: 500px) {
    width: 100%;
  }
`;

const NewsletterCaption = styled.div``;

const NewsletterSocial = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  img {
    height: 30px;
    margin-right: 5px;
  }
  @media (max-width: 750px) {
    width: 100%;
  }
`;

const Divider = styled.div`
  width: 10%;
  height: 2px;
  background: #454545;
  margin: 7px 0;
`;

const Copyright = styled.div`
  width: 100%;
  padding: 20px;
  font-size: 12px;
  color: #454545;
  text-align: center;
`;

export default function MainFooter() {
  return (
    <div>
      <MainFooterHolder>
        <FooterContent>


        </FooterContent>
      </MainFooterHolder>
      <Copyright>
        Â© 2018 - TODOS OS DIREITOS RESERVADOS - EDITORA SANAR LTDA - ME. CNPJ:
        18.990.682/0001-92
      </Copyright>
    </div>
  );
}
